public void addValue(final Option option,
                         final Object value) {
        if (option instanceof Argument) {
            addOption(option);
        }

        List valueList = (List) values.get(option);

        if (valueList == null) {
            valueList = new ArrayList();
            values.put(option, valueList);
        }

        valueList.add(value);
    }
