public static String capitalize(CharSequence cs) {
        if (cs == null ) {
            return null;
        }
        int strLen;
        if ((strLen = cs.length()) == 0) {
            return cs.toString();
        }
        return new StringBuilder(strLen)
            .append(Character.toTitleCase(cs.charAt(0)))
            .append(CharSequenceUtils.subSequence(cs, 1))
            .toString();
    }
